(1, '2020', '76.01', '1.03.0.00.0.00.01.0000', '02 TAHUN 2020', '2020-01-03', 'Pengangkatan Pejabat Pembuat Komitmen (PPK) Konstruksi Dan  Pengangkatan Pejabat Pembuat Komitmen (PPK) Konsultansi Dinas Pekerjaan Umum Dan Penataan Ruang Kabupaten Pasangkayu Tahun 2020', '197508022006041011', 'kepala dinas', 'Pembina Tingkat I, IV/b', 'Budiyansa, ST', '{\"0\": {\"nip\": \"198006152006041018\", \"nama\": \"Sumarlin, ST\", \"jabatan\": \"Sekretaris Dinas\", \"pangkat\": \"Pembina , IV/a\", \"jabatan_sk\": \"PPK Cipta Karya dan Sekretariat\"}, \"1\": {\"nip\": \"197308142005021003\", \"nama\": \"Kamaruddin, ST\", \"jabatan\": \"Kepala Bidang Pembinaan Jasa Konstruksi\", \"pangkat\": \"Pembina , IV/a\", \"jabatan_sk\": \"PPK Pembinaan Jasa Konstruksi\"}, \"2\": {\"nip\": \"197101152007011019\", \"nama\": \"Herry Hasanuddin. M, ST\", \"jabatan\": \"Kepala Bidang Penataan Ruang\", \"pangkat\": \"Penata Tingkat I, III/d\", \"jabatan_sk\": \"PPK Penataan Ruang\"}, \"3\": {\"nip\": \"197501192007011013\", \"nama\": \"Muammar Nur, ST\", \"jabatan\": \"Kepala Bidang Binamarga\", \"pangkat\": \"Penata Tingkat I, III/d\", \"jabatan_sk\": \"PPK Binamarga\"}, \"4\": {\"nip\": \"197908252009031003\", \"nama\": \"Pusmahasib, S.Si\", \"jabatan\": \"Plt. Kepala Bidang Sumber Daya Air\", \"pangkat\": \"Penata , III/c\", \"jabatan_sk\": \"PPK Sumber Daya Air\"}}', 0, '{\"0\": {\"isi\": \"Bahwa untuk pelaksanaan proses kerja pengadaan barang / jasa pemerintah yang efektif, efisien dan akuntabel pada Dinas Pekerjaan Umum dan Penataan Ruang Pemerintah Daerah Kabupaten Pasangkayu, maka dipandang perlu mengangkat Pejabat Pembuat Komitmen (PPK) pada Dinas Pekerjaan Umum Kabupaten Pasangkayu\"}, \"1\": {\"isi\": \"bahwa yang tersebut namanya dalam lampiran keputusan ini dipandang cakap dan mampu melaksanakan tugas\"}, \"2\": {\"isi\": \"bahwa pengangkatan sebagaimana dimaksud pada point a perlu ditetapkan dalam Keputusan Kepala Dinas\"}}', '{\"0\": {\"isi\": \"Undang-Undang Nomor 28 Tahun 1999 tentang Penyelenggaraan Pemerintahan yang bersih dan bebas Korupsi, Kolusi dan Nepotisme (Lembaran Negara Republik Indonesia Tahun 1999 Nomor 75, Tambahan Lembaran Negara Republik Indonesia Nomor 3581)\"}, \"1\": {\"isi\": \"Undang-Undang Nomor 28 tahun 2000 tentang usaha dan peran masyarakat Jasa Konstruksi (Lembaran Negara Republik Indonesia tahun 2000 Nomor 63)\"}, \"2\": {\"isi\": \"Undang-Undang Nomor 7 Tahun 2003 tentang Pembentukan Kabupaten Luwu Timur dan Kabupaten Mamuju Utara di Provinsi Sulawesi Selatan (Lembaran Negara Tahun 2003 Nomor 27, Tambahan Lembaran Negara Nomor 4270)\"}, \"3\": {\"isi\": \"Undang-Undang Nomor 17 Tahun 2003 tentang Keuangan Negara (Lembaran Negara Republik Indonesia Tahun 2003 Nomor 47, Tambahan Lembaran Negara Republik Indonesia Nomor 4286)\"}, \"4\": {\"isi\": \"Undang-Undang Nomor 1 Tahun 2004 tentang Perbendaharaan Negara (Lembaran Negara Republik Indonesia Tahun 2004 Nomor 5, Tambahan Lembaran Negara Republik Indonesia Nomor 4355)\"}, \"5\": {\"isi\": \"Undang-Undang Nomor 32 Tahun 2004 tentang Pemerintahan Daerah (Lembaran Negara Republik Indonesia Tahun 2004 Nomor 125, Tambahan Lembaran Negara Republik Indonesia Nomor 4437) sebagaimana telah diubah dengan Undang-Undang Nomor 8 Tahun 2005 tentang Penetapan Peraturan Pemerintah Pengganti Undang-Undang Nomor 3 Tahun 2005 tentang Perubahan Undang-Undang Nomor 32 Tahun 2004 tentang Pemerintahan Daerah menjadi Undang-Undang (Lembaran Negara Republik Indonesia Tahun 2005 Nomor 108, Tambahan Lembaran Negara Republik Indonesia Nomor 4548)\"}, \"6\": {\"isi\": \"Undang-Undang Nomor 33 Tahun 2004 tentang Perimbangan Keuangan Antara Pemerintah Pusat dan Pemerintah Daerah (Lembaran Negara Republik Indonesia Tahun 2004 Nomor 126, Tambahan Lembaran Negara Republik Indonesia Nomor 4438)\"}, \"7\": {\"isi\": \"Undang-Undang Nomor 2 Tahun 2017 tentang Jasa Konstruksi\"}, \"8\": {\"isi\": \"Peraturan Presiden Republik Indonesia Nomor 16 Tahun 2018 tentang Pengadaan Barang/Jasa Pemerintah\"}, \"9\": {\"isi\": \"Peraturan Menteri Dalam Negeri Nomor 13 Tahun 2006 tentang Pedoman Pengelolaan Keuangan Daerah sebagaimana telah diubah dengan Peraturan Menteri Dalam Negeri Nomor 59 Tahun 2007 tentang Perubahan Atas Peraturan Menteri Dalam Negeri Nomor 13 Tahun 2006 tentang Pedoman Pengelolaan Keuangan Daerah\"}, \"10\": {\"isi\": \"Peraturan Menteri Dalam Negeri Nomor 33 Tahun 2019 tentang Pedoman Penyusunan Anggaran Pendapatan dan Belanja Daerah Tahun Anggaran 2020\"}, \"11\": {\"isi\": \"Peraturan Daerah Kabupaten Mamuju Utara Nomor 5 Tahun 2006 tentang pokok-pokok Pengelolaan Keuangan Daerah (Lembaran Daerah Nomor 5 Tahun 2006, Tambahan Lembaran Daerah Nomor 5)\"}, \"12\": {\"isi\": \"Peraturan Daerah Kabupaten Pasangkayu Nomor 35 Tahun 2016 tentang Pembentukan Organisasi Perangkat Daerah\"}, \"13\": {\"isi\": \"Peraturan Daerah Kabupaten Pasangkayu Nomor 10 Tahun 2019 tanggal 30 Desember 2019 tentang Anggaran pendapatan dan Belanja Daerah Tahun Anggaran 2020\"}, \"14\": {\"isi\": \"Peraturan Bupati Kabupaten Pasangkayu Nomor 47 Tahun 2018 tanggal 30 Desember 2019 tentang Penjabaran Anggaran Pendapatan dan Belanja Daerah Kebupaten Pasangkayu Tahun Anggaran 2020\"}}', '{\"0\": {\"isi\": \"Tugas Pejabat sebagaimana dimaksud pada diktum Kesatu adalah :\", \"p_l\": \"P\"}, \"1\": {\"isi\": \"menyusun perencanaan pengadaan\", \"p_l\": \"L\"}, \"2\": {\"isi\": \"menetapkan spesifikasi teknis/Kerangka Acuan Kerja (KAK)\", \"p_l\": \"L\"}, \"3\": {\"isi\": \"menetapkan rancangan kontrak\", \"p_l\": \"L\"}, \"4\": {\"isi\": \"menetapkan HPS\", \"p_l\": \"L\"}, \"5\": {\"isi\": \"menetapkan besaran uang muka yang akan dibayarkan kepada Penyedia\", \"p_l\": \"L\"}, \"6\": {\"isi\": \"mengusulkan perubahan jadwal kegiatan\", \"p_l\": \"L\"}, \"7\": {\"isi\": \"menetapkan tim pendukung\", \"p_l\": \"L\"}, \"8\": {\"isi\": \"menetapkan tim atau tenaga ahli\", \"p_l\": \"L\"}, \"9\": {\"isi\": \"melaksanakan E-purchasing untuk nilai paling sedikit di atas Rp200.000.000,00 (dua ratus juta rupiah)\", \"p_l\": \"L\"}, \"10\": {\"isi\": \"menetapkan Surat Penunjukan Penyedia Barang/Jasa\", \"p_l\": \"L\"}, \"11\": {\"isi\": \"mengendalikan Kontrak\", \"p_l\": \"L\"}, \"12\": {\"isi\": \"melaporkan pelaksanaan dan penyelesaian kegiatan kepada PA/KPA\", \"p_l\": \"L\"}, \"13\": {\"isi\": \"menyerahkan hasil pekerjaan pelaksanaan kegiatan kepada PA/KPA dengan berita acara penyerahan\", \"p_l\": \"L\"}, \"14\": {\"isi\": \"menyimpan dan menjaga keutuhan seluruh dokumen pelaksanaan kegiatan\", \"p_l\": \"L\"}, \"15\": {\"isi\": \"menilai kinerja Penyedia\", \"p_l\": \"L\"}}', '{\"0\": {\"isi\": \"Selain tugas pokok dan kewenangan sebagaimana dimaksud, dalam hal diperlukan, PPK,dapat\", \"p_l\": \"P\"}, \"1\": {\"isi\": \"melakukan tindakan yang mengakibatkan pengeluaran anggaran belanja\", \"p_l\": \"L\"}, \"2\": {\"isi\": \"mengadakan dan menetapkan perjanjian dengan pihak lain dalam batas anggaran belanja yang telah ditetapkan\", \"p_l\": \"L\"}, \"3\": {\"isi\": \"PPK dalam melaksanakan tugas sebagaimana dimaksud dapat dibantu oleh Pengelola Pengadaan Barang/Jasa\", \"p_l\": \"L\"}, \"4\": {\"isi\": \"Melaporkan kemajuan pekerjaan termasuk penyerapan anggaran dan hambatan pelaksanaan pekerjaan kepada PA/KPA setiap triwulan\", \"p_l\": \"L\"}, \"5\": {\"isi\": \"Menyimpan dan menjaga keutuhan seluruh dokumen pelaksanaan Pengadaan barang/Jasa Pemerintah\", \"p_l\": \"L\"}}', '{\"0\": {\"isi\": \"Segala biaya yang timbul akibat keputusan ini dibebankan kepada APBD Dinas Pekerjaan Umum dan Penataan Ruang Kabupaten Pasangkayu Tahun Anggaran 2020.\", \"p_l\": \"P\"}}', '{\"0\": {\"isi\": \"Keputusan ini mulai berlaku pada tanggal ditetapkan dan apabila terdapat kekeliruan didalamnya akan diperbaharui sebagaimana mestinya.\", \"p_l\": \"P\"}}', '{\"0\": {\"tembusan\": \"Bupati                                            Pasangkayu, di Pasangkayu\"}, \"1\": {\"tembusan\": \"Sekretaris Daerah Kabupaten Pasangkayu di Pasangkayu\"}, \"2\": {\"tembusan\": \"Inspektur Inspektorat Kabupaten Pasangkayu di Pasangkayu\"}, \"3\": {\"tembusan\": \"Kepala Badan PKAD Kabupaten Pasangkayu di Pasangkayu\"}, \"4\": {\"tembusan\": \"Masing-masing yang bersangkutan\"}, \"5\": {\"tembusan\": \"Pertinggal\"}}', '', '', '0000-00-00 00:00:00', '', '0000-00-00 00:00:00', '', 0),
